INSERT INTO `proposal_pfb` VALUES ('13', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '1', '2', '提案内容符合法律法规及区情实际', '10');
INSERT INTO `proposal_pfb` VALUES ('14', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '1', '3', '提案所反映问题客观存在', '10');
INSERT INTO `proposal_pfb` VALUES ('15', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '1', '4', '提案对问题的分析恰如其分，找到了问题的症结', '10');
INSERT INTO `proposal_pfb` VALUES ('16', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '1', '5', '提案文本有情况、有分析、有建议，结构完整，内容精练集中，符合“一事一案”原则', '10');
INSERT INTO `proposal_pfb` VALUES ('17', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '1', '6', '提案者积极配合参与办理过程，沟通畅通', '10');
INSERT INTO `proposal_pfb` VALUES ('18', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '1', '7', '提案建议可行，目前具有可操作性。按建议可吸收采纳度评分（视情况可分全部采纳25-30、大部分采纳20-25、小部分采纳15-20和基本不采纳0-15四个档次评价打分）', '30');
INSERT INTO `proposal_pfb` VALUES ('19', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '1', '8', '提案能推动部门相关方面工作，按推动作用大小评分（可分有较大推动作用8-10、有一定推动作用5-8、几乎没有作用1-5四个档次评价打分）', '10');
INSERT INTO `proposal_pfb` VALUES ('20', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '2', '1', '办理单位高度重视，办理工作态度端正、认真负责。', '10');
INSERT INTO `proposal_pfb` VALUES ('21', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '2', '2', '以上门走访、面商座谈、调研视察等多种形式主动邀请了提案者开展面对面沟通协商。办前、办中、办后沟通充分，办理正式复文前征求了提案者意见。', '10');
INSERT INTO `proposal_pfb` VALUES ('22', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '2', '3', '全部提案在办理时限内以正式公文处理程序办复，网上和纸质办复文件格式规范、语言准确。', '10');
INSERT INTO `proposal_pfb` VALUES ('23', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '2', '4', '提案复文体现了（主、协办）各方意见，对提案所反映的问题回复针对性较强。', '10');
INSERT INTO `proposal_pfb` VALUES ('24', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '2', '5', '有条件解决的问题，已解决或基本解决(A)。因条件所限暂时难以解决的问题，已制定计划，积极创造条件逐步解决(B)。确实不能解决的问题，已向提案者说明了情况(C)。', '40');
INSERT INTO `proposal_pfb` VALUES ('25', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '2', '6', '重点提案办理效果体现在对工作的明显改进和推动上，相关基层和群众反映良好。如非重点提案，此项得满分', '10');
INSERT INTO `proposal_pfb` VALUES ('26', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '2', '7', '二次办理提案跟踪办理效果好，落实度有提高。如非二次办理，此项得满分', '5');
INSERT INTO `proposal_pfb` VALUES ('27', '1', '2018-11-07 16:46:23', '1', '2018-11-07 16:46:23', '1', '2', '8', '建立提案办理公开机制，按规定对涉及公共利益、公众权益、社会关切及需要社会广泛知晓且不涉密不敏感的提案办理复文全文公开（在复文中告知了公开方式、地点），公开内容未造成不良社会影响。涉密或因其它原因不宜公开的，已作出合理说明。', '5');
